import React, { useRef, useState } from "react";
import profileImg from "../assets/images/ishwore.jpg";
import { Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { setting } from '@fortawesome/fa-sharp fa-solid fa-gear';

const ProfileMenu = () => {
    const auth = localStorage.getItem('user');
    const [open, setOpen] = useState(false);
    const navigate = useNavigate();
    // const Menus = ["Profile", "Settings", "Logout"];
    const logout = () => {
        localStorage.clear();
        navigate('/');
    }

    const menuRef = useRef();
    const imgRef = useRef();
    window.addEventListener('click', (e) => {
        if (e.target !== menuRef.current && e.target !== imgRef.current) {
            setOpen(false);
        }
    })
    return (

        <div className="pt-2 pl-10 mr-2" >
            <div className="relative">
                <img
                    ref={imgRef}
                    onClick={() => setOpen(!open)}
                    className="rounded-full mx-0  w-12 h-12 object-cover  cursor-pointer" src={profileImg} alt="" />
            </div>
            {
                open &&
                <div ref={menuRef} className="bg-slate-300 my-3 mx-2 p-3 w-72 shadow-lg text-center  absolute -right-1 rounded-2xl">
                    <ul onClick={() => setOpen(false)}  >
                        {
                            // Menus.map((menu)=>( 
                            //     <li
                            //     onClick={()=> setOpen(false)}
                            //      className="p-2 text-lg cursor-pointer rounded-2xl hover:bg-slate-400 hover:font-semibold" key = {menu}>{menu}</li>
                            // ))
                            <>
                                <li className="p-2" ><Link  to='/profile'><button className="text-base cursor-pointer rounded-2xl w-60 h-12 hover:bg-slate-400 hover:font-semibold">{JSON.parse(auth).name}</button></Link></li>
                                <li className="p-2" ><Link  to='/settings'><button className="text-base cursor-pointer rounded-2xl w-60 h-12 hover:bg-slate-400 hover:font-semibold" >Settings</button></Link></li>
                                <li className="p-2" ><button className="text-base cursor-pointer rounded-2xl w-60 hover:bg-slate-400 h-12 hover:font-semibold" onClick={logout} ><FontAwesomeIcon icon="setting" spin />Logout</button></li>

                            </>}
                    </ul>
                </div>
            }
        </div>

    )

}


export default ProfileMenu;
