import React, { useState } from 'react';

const ProfileComponent = () => {
    const [showChangePassword, setShowChangePassword] = useState(false);
    const [showProfileUpdate, setShowProfileUpdate] = useState(false);

    const handleShowChangePassword = () => {
        setShowChangePassword(true);
        setShowProfileUpdate(false);
    };

    const handleShowProfileUpdate = () => {
        setShowChangePassword(false);
        setShowProfileUpdate(true);
    };

    return (
        <div className='mt-20 inline-grid'>
            <div className="flex">
                {/* First Column - 10% */}
                <div className="w-1/10 bg-gray-100 p-4 inline-grid">
                    <button
                        className="bg-blue-500 text-white py-2 px-4 rounded-md mb-2"
                        onClick={handleShowChangePassword}
                    >
                        Change Password
                    </button>
                    <button
                        className="bg-green-500 text-white py-2 px-4 rounded-md"
                        onClick={handleShowProfileUpdate}
                    >
                        Profile Update
                    </button>
                </div>

                {/* Second Column - 90% */}
                <div className="w-9/10 bg-white p-4">
                    {showChangePassword && (
                        <div>
                            {/* Interface for Change Password */}
                            <h2 className="text-xl font-bold mb-4">Change Password</h2>
                            {/* Add your change password interface here */}
                        </div>
                    )}

                    {showProfileUpdate && (
                        <div>
                            {/* Interface for Profile Update */}
                            <h2 className="text-xl font-bold mb-4">Profile Update</h2>
                            {/* Add your profile update interface here */}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ProfileComponent;
