import React, { useState, useEffect, useRef } from "react";

const VehicleList = () => {
    const [vehicles, setVehicles] = useState([]);
    const resultData = useRef([]);

    useEffect(() => {
        const getVehicles = async () => {
            const result = await fetch('http://localhost:5000/api/product', {
                method: 'get',
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            const data = await result.json();
            resultData.current = data;
            setVehicles(data);
        };

        getVehicles();
    }, []);

    console.warn("vehicles", vehicles);

    return (
        <form className="inline-grid mt-28 rounded-3xl justify-center bg-stone-200">
            <div className="mt-2">
                <h1 className='text-center font-bold text-2xl text-stone-600'>User Details</h1>
            </div>
            <div>
                <table className='border border-collapse my-5'>
                    <thead>
                        <tr>
                            <th className='px-4 border'>Vehicle ID</th>
                            <th className='px-4 border'>Vehicle Name</th>
                            <th className='px-4 border'>Category</th>
                            <th className='px-4 border'>Image</th>
                            <th className='px-4 border'>RegistrationNo</th>
                            <th className='px-4 border'>Quntity</th>
                            <th className='px-4 border'>Rent Price</th>
                            <th className='px-4 border'>Reviews Rating</th>
                            <th className='px-4 border'>Reviews Number</th>
                            <th className='px-4 border'> Description</th>
                            <th className='px-4 border'>Actions</th>
                        </tr>
                    </thead>
                    <tbody className="text-sm">
                        {resultData.current((result) => (
                            <tr key={result._id}>
                                <td className='px-4 border'>{result._id}</td>
                                <td className='px-4 border'>{result.name}</td>
                                <td className='px-4 border'>{result.category}</td>
                                <td className='px-4 border'>{result.image}</td>
                                <td className='px-4 border'>{result.registrationNo}</td>
                                <td className='px-4 border'>{result.countInStock}</td>
                                <td className='px-4 border'>{result.price}</td>
                                <td className='px-4 border'>{result.rating}</td>
                                <td className='px-4 border'>{result.numReviews}</td>
                                <td className='px-4 border'>{result.description}</td>
                                <td className='px-4 border text-white'>
                                    <button className='mx-2 bg-stone-600 rounded-xl px-4 font-medium hover:bg-stone-700 hover:font-bold'>Edit</button>
                                    <button className='mx-2 bg-red-600 rounded-xl px-4 font-medium hover:bg-red-700'>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </form>
    );
};

export default VehicleList;
