import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';

const ViewVehicle = () => {
    const [category, setcatName] = useState("");
    const [name, setName] = useState("");
    const [countInStock, setQty] = useState("");
    const [registrationNo, setRegNo] = useState("");
    const [price, setPrice] = useState("");
    const [description, setDes] = useState("");
    const [Image, setImage] = useState("");
    const params = useParams();
    useEffect(() => {
        getVehicleDetails();
    })
    console.log(category, name, countInStock, registrationNo, price, description, Image);
    const getVehicleDetails = async () => {
        // console.log(params);
        const result = await fetch(`http://localhost:5000/api/product/${params.id}`, {
            method: 'get',
            headers: {
                'Content-Type': 'application/json'
            }
        });
        const resultData = await result.json();
        // console.log(resultData);
        setcatName(resultData.category);
        setName(resultData.name);
        setQty(resultData.countInStock);
        setRegNo(resultData.registrationNo);
        setPrice(resultData.price);
        setDes(resultData.description);
        setImage(resultData.image);

    }
    const getImageUrl = (imageName) => {
        return `http://localhost:5000${imageName}`;
    };

    return (
        <form className="inline-grid mt-16 rounded-3xl justify-center">
            <div className=" mt-4">
                <img
                    src={getImageUrl(Image)}
                    alt={name}
                    className="w-max h-96 object-cover mb-4 rounded-md"
                />
                <div className="font-bold  inline-grid ">
                    <span className="text-2xl mt-3">Name : {name.toUpperCase()}</span>
                    <span className="text-xl mt-3">Category : {category}</span>
                    <span className="text-xl mt-3">RegistrationNo : {registrationNo}</span>
                    <span className="text-xl mt-3">Rent Price Per Day : {price}</span>
                    <span className="text-xl mt-3">Stock: {countInStock}</span>
                    <span className="text-xl mt-3">Description : {description}</span>
                </div>

            </div>
        </form>
    )
}

export default ViewVehicle;
