import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
const CardList = () => {
    const auth = localStorage.getItem('user');
    const [cards, setCards] = useState([]);
    const navigate = useNavigate();
    useEffect(() => {
        if (!(auth)) {
            navigate('/login');
        }

        const fetchCards = async () => {
            try {
                const response = await fetch("http://localhost:5000/api/card");
                const data = await response.json();
                setCards(data);
            } catch (error) {
                console.error("Error fetching vehicles:", error);
            }
        };

        fetchCards();


    }, [auth, navigate])
    const getImageUrl = (imageName) => {
        return `http://localhost:5000${imageName}`;
    };

    return (
        <form className="inline-grid mt-10 ">
            <div className="mt-12">
                <h1 className="text-red-400 font-bold text-2xl">My Card List</h1>
                <div className="bg-slate-400 ">
                    <div className="grid grid-cols-3 gap-4 mx-10">
                        {cards.length > 0 ? <> {
                            cards.map((card) => (
                                <div
                                    key={card._id}
                                    className="mt-8 bg-stone-200 p-4 rounded-lg shadow-lg  "
                                >
                                    <img
                                        src={getImageUrl(card.image)}
                                        alt={card.name}
                                        className="w-full h-60 object-cover mb-4 rounded-md"
                                    />
                                    <h2 className="text-xl font-semibold mt-2 ">
                                        {card.name.toUpperCase()}
                                    </h2>
                                    <p className="mt-2">{card.description}</p>
                                    <p className=" bg-sky-400 h-10 pt-2 mt-2 rounded-sm "> Rent Price Per Day: NRs {card.price}</p>
                                    <div className="flex justify-between mt-4">
                                        <button
                                            className="bg-amber-600 text-white py-2 px-4 rounded-md font-medium hover:bg-amber-700 hover:font-bold"
                                        >
                                            Add Card
                                        </button>
                                        <Link to={`/view/${card._id}`}>
                                            <button
                                                className="bg-red-700 text-white py-2 px-4 rounded-md font-medium hover:bg-red-800 hover:font-bold"
                                            >
                                                Book Now
                                            </button>
                                        </Link>
                                    </div>
                                </div>
                            ))
                        }</>
                            :
                            <h1 className="mt-20 text-5xl  text-red-600">No card available !</h1>
                        }
                        :
                    </div>
                </div>
            </div>
        </form>
    )
}


export default CardList;
