import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Carousel = () => {
    const [vehicles, setVehicles] = useState([])

    useEffect(() => {

        const fetchVehicles = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/product`);
                const data = await response.json();
                setVehicles(data);

                // setImages(data);
                // console.log(key, data);
            } catch (error) {
                console.error("Error fetching vehicles:", error);
            }
        };
        fetchVehicles()
    }, []);
    const getImageUrl = (imageName) => {
        return `http://localhost:5000${imageName}`;
    };
    const [currentIndex, setCurrentIndex] = useState(0);

    const nextSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % vehicles.length);
    };

    const prevSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + vehicles.length) % vehicles.length);
    };

    return (
        <div className="mt-30">
            <button onClick={prevSlide} className="absolute top-1/2 transform -translate-y-1/2 left-4 text-2xl z-10">
                &#10094;
            </button>
            <button onClick={nextSlide} className="absolute top-1/2 transform -translate-y-1/2 right-4 text-2xl z-10">
                &#10095;
            </button>
            <div className=" flex items-center justify-center relative">
                {vehicles.map((vehicle, index) => (
                    <Link to={`/view/${vehicle._id}`} >
                        <div
                            key={index}
                            className={`none opacity 0.5s ease-in-out ${index === currentIndex ? 'block' : ''}`}
                        >
                            <img src={getImageUrl(vehicle.image)} alt={`Slide ${index}`} className="w-full h-auto" />
                        </div>
                    </Link>
                ))}
            </div>
        </div>
    );
};

export default Carousel;
