import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"
import profileImg from "../assets/images/userIcon.jpg";


const ViewUser = () => {
    const [name, setName] = useState('');
    const [image, setImage] = useState('');
    const [email, setEmail] = useState('');
    const auth = localStorage.getItem('user');
    const params = useParams();
    useEffect(() => {
        const getUsers = async () => {

            const result = await fetch(`http://localhost:5000/api/users/${params.id}`, {
                method: 'get',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${JSON.parse(auth).token}`,
                }
            });


            const data = await result.json();
            // console.log(data);
            setName(data.name);
            setImage(data.image);
            setEmail(data.email);

        };

        getUsers();
    }, [auth, params.id]);

    const getImageUrl = (imageName) => {
        return `http://localhost:5000${imageName}`;
    };

    console.log(name, image, email);
    return (
        <div className="inline-grid mt-20 w-full ">
            <div className=" mt-4">
                <h1 className='text-center font-extrabold text-2xl text-stone-600 '>View User Details</h1>

                <div>
                    <p> {image !== '' ? <img
                        className="rounded-full mx-0 mt-2 w-60 h-56 object-cover  cursor-pointer" src={getImageUrl(image)} alt="" /> : <img
                        className="rounded-full mx-0 mt-2 w-60 h-56 object-cover  cursor-pointer" src={profileImg} alt="" />} </p>
                    <p className='mt-2'>Name: {name}</p>
                    <p className=' mt-3'>Email: {email}</p>
                </div>

            </div>
        </div>

    )


}

export default ViewUser;
