import React, { useState, useEffect } from "react";
import { Link, useNavigate } from 'react-router-dom';


const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();
    useEffect(() => {

        const auth = localStorage.getItem('user');
        if (auth) {
            navigate('/');
        }
    })
    const handleLogin = async () => {
        console.warn("email,password", email, password);
        const userLogin = await fetch("http://localhost:5000/api/users/login", {
            method: 'post',
            body: JSON.stringify({ email, password }),
            headers: {
                'Content-Type': 'application/json'
            },
        });
        const resultLogin = await userLogin.json();
        console.warn(resultLogin);
        if (resultLogin) {
            localStorage.setItem("user", JSON.stringify(resultLogin));
            navigate("/");
        } else {
            alert("Please enter correct details");
        }
    }
    return (
        // <form  className="inline-grid mt-16 rounded-3xl w-96  justify-center bg-stone-200">
        //         <div className=" mt-4">
        //             <h1 class='text-center font-extrabold text-2xl text-stone-600 '>Login</h1>
        //         </div>
        //         <div className=" inline-grid mt-6">
        //             <label class="text-left font-bold text-stone-500 ">*Email*</label>
        //             <input class=" w-72 outline outline-offset-2 outline-2 text-center rounded-md mt-2 " type="email" value={email} onChange={(e) => setEmail(e.target.value)}  placeholder="Enter your Email" required/>
        //         </div>
        //         <div className=" inline-grid mt-2">
        //             <label class="text-left font-bold text-stone-500">*Password*</label>
        //             <input class=" w-72 outline outline-offset-2 outline-2 text-center rounded-md mt-2" type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Enter your Password" required/>
        //         </div>
        //         <button type="submit" onClick={handleLogin} class=' w-72 rounded-full h-12 text-center font-semibold mt-8 mb-3 bg-stone-400 hover:bg-stone-500 hover:text-lg hover:font-bold' value="Login" />
        //         <div className="mt-2 mb-6">
        //             <div className="flex text-sm"> 
        //                 <p  className="">Create new account ?</p> 
        //                 <Link  className="underline" to="/signup" >SignUp</Link>
        //                 <Link className="underline ml-3" to="/forgetpassword" >ForgetPassword ?</Link>
        //             </div>
        //         </div>  
        //     </form>
        <form onSubmit={handleLogin} className=" inline-grid  mt-16 rounded-3xl w-96 justify-center bg-stone-200">
            <div className="mt-4">
            <h1 className='text-center font-extrabold text-2xl text-stone-600 '>Login  </h1>
            </div>
            <div className="inline-grid mt-6">
            <label className="text-left font-bold text-stone-500">*Email*</label>
            <input type='email' className=" w-72 outline outline-offset-2 outline-2 text-center rounded-md mt-2 " placeholder='Enter your email' value={email} onChange={(e) => setEmail(e.target.value)} />
            </div>
            <div div className="inline-grid mt-2">
            <label class="text-left font-bold text-stone-500">*Password*</label>
            <input type='password' class=" w-72 outline outline-offset-2 outline-1  rounded-md mt-2" placeholder='Enter your password' value={password} onChange={(e) => setPassword(e.target.value)} />
            </div>
            <input type="submit" class=' w-72 rounded-full h-12 text-center font-semibold mt-8 mb-3 bg-stone-400 hover:bg-stone-500 hover:text-lg hover:font-bold' value="Login" />
            <div className="mt-2 mb-6">
                <div className="flex text-sm">
                    <p className="">Create new account ?</p>
                    <Link className="underline" to="/signup" >SignUp</Link>
                    <Link className="underline ml-3" to="/forgetpassword" >ForgetPassword ?</Link>
                </div>
            </div>
        </form>
    )
}

export default Login;
