import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from "react-router-dom";


const ViewBooking = () => {

    const navigate = useNavigate();
    const auth = localStorage.getItem('user');
    const params = useParams();
    const [bookingDetail, setbookingDetail] = useState([]);
    // const [message, showMessage] = useState("");
    useEffect(() => {
        if (!(auth)) {
            navigate('/login');
        }

        const fetchMyBookingDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/booking/${params.id}`, {
                    method: 'get',
                    headers: {
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${JSON.parse(auth).token}`,
                    }
                });
                const data = await response.json();
                setbookingDetail(data);
                console.log(data);

            } catch (error) {
                console.error("Error fetching vehicles:", error);
            }
        };

        fetchMyBookingDetails();
    })

    // console.warn(params.id);

    const getImageUrl = (imageName) => {
        return `http://localhost:5000${imageName}`;
    };
    return (
        <div className="inline-grid mt-20 ">
            <div className=" mt-4">
                <h1 className='text-center font-extrabold text-2xl text-stone-600 '>View Booking Details</h1>
            </div>
            <div className="flex bg-slate-800 ">
                {/* First Column - 10% */}

                <div className="w-4/12  ">

                    <img
                        src={getImageUrl(bookingDetail.bookingItems.image)}
                        alt=""
                        className="w-max h-96 rounded-xl object-cover px-5 py-4 bg-stone-200"
                    />
                </div>
                <div className="w-4/12  ">
                    <h2 className='text-yellow-400 mt-3 font-bold text-2xl'>Usefull Links</h2>

                </div>
                <div className="w-4/12  ">
                    <h2 className='text-yellow-400 mt-3 font-bold text-2xl'>Contact Us</h2>


                </div>
            </div>
        </div>

    )
}



export default ViewBooking;
