import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Billing = () => {
    const auth = localStorage.getItem('user');
    const vehicleData = localStorage.getItem('vehicleDetails');
    const book = localStorage.getItem('bookingData');
    const vehicle = JSON.parse(vehicleData);
    const bookingData = JSON.parse(book);
    console.log(bookingData);
    // console.log(category);
    const navigate = useNavigate();
    useEffect(() => {
        if (!auth) {
            navigate('/login');
            // alert("You are not Login !");
        }


    });


    return (
        <div>
            <div className="mt-20 inline-grid">
                <h2> Your Booking Details</h2>

                <div className="grid grid-cols-2 gap-6 w-screen  mt-5 ">
                    <div className="col-span-1 ">
                        <h2 className="text-start ml-2 text-xl">Mr/Mrs <span className="font-semibold">{JSON.parse(auth).name}</span>, </h2>
                        <p className="text-lg text-right">Hello,<span className=" font-bold"> Mr/Mrs.{JSON.parse(auth).name}</span> thank you choosing our organization for Rent. We will give free <span className=" font-bold">Deriver</span> for <span className=" font-bold">{vehicle.category}</span> . If you want to book {vehicle.category} click on <samp className=" font-bold"> Ok </samp> otherwise click on <samp className=" font-bold"> Cancel </samp> </p>
                    </div>

                    <div className="font-bold inline-grid col-span-1 text-left ">
                        <h4 className=" text-center">Bill Details</h4>
                        <table className='border-collapse border border-slate-950 my-5  '>
                            <tbody className=" text-center">
                                <tr>
                                    <td>Rent Price (per day):</td>
                                    <td>Rs. {vehicle.price}</td>
                                </tr>
                                <tr>
                                    <td>Rent Day(s):</td>
                                    <td>{bookingData.days}</td>
                                </tr>
                                <tr>
                                    <td>Vehicle Quntity:</td>
                                    <td>{bookingData.v_quantity}</td>
                                </tr>
                                <tr>
                                    <td>Total Rent Price:</td>
                                    <td>{bookingData.totalRent}</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Billing;
